import { Button, TextField } from "@mui/material";
import { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useUser } from "../contexts/user.context";

const Signup = () => {
  const { emailPasswordSignup } = useUser();
  const navigate = useNavigate();
  const location = useLocation();

  const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: ""
  });

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const onFormInputChange = (event) => {
    const { name, value } = event.target;
    setForm({ ...form, [name]: value });
  };

  const redirectNow = () => {
    navigate('/Login');
  }

  const onSubmit = async (event) => {
    event.preventDefault();
    if (form.password !== form.confirmPassword) {
      alert("Passwords do not match.");
      return;
    }

    if (!emailRegex.test(form.email)) {
      alert("Please enter a valid email address.");
      return;
    }

    try {
      const user = await emailPasswordSignup(form.email, form.password, form.username);
      if (user) {
        redirectNow();
      }
    } catch (error) {
      alert(error);
    }
  };

  return (
    <form style={{ display: "flex", flexDirection: "column", maxWidth: "300px", margin: "auto" }}>
      <h1>Signup</h1>
      <TextField
        label="Username"
        type="text"
        variant="outlined"
        name="username"
        value={form.username}
        onInput={onFormInputChange}
        style={{ marginBottom: "1rem" }}
      />
      <TextField
        label="Email"
        type="email"
        variant="outlined"
        name="email"
        value={form.email}
        onInput={onFormInputChange}
        style={{ marginBottom: "1rem" }}
      />
      <TextField
        label="Password"
        type="password"
        variant="outlined"
        name="password"
        value={form.password}
        onInput={onFormInputChange}
        style={{ marginBottom: "1rem" }}
      />
      <TextField
        label="Confirm Password"
        type="password"
        variant="outlined"
        name="confirmPassword"
        value={form.confirmPassword}
        onInput={onFormInputChange}
        style={{ marginBottom: "1rem" }}
      />
      <Button variant="contained" color="primary" onClick={onSubmit}>
        Signup
      </Button>
      <p className="disclaimer">
        Do not enter your actual credentials. Enter fake but memorable credentials. For example, email: admin@gmail.com, username=admin, password=admin. The above example credentials are already in use.
      </p>
    </form>
  );
}

export default Signup;
