import { Button, TextField } from "@mui/material";
import { useContext, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useUser, UserContext } from "../contexts/user.context";

const Signup = () => {
  const { emailPasswordSignup } = useUser();
 const navigate = useNavigate();
 const location = useLocation();

 // As explained in the Login page.
 //const { emailPasswordSignup } = useContext(UserContext);
 const [form, setForm] = useState({
   username: "", // Add this line for the username
   email: "",
   password: "",
   confirmPassword: "" // Add this line
 });

 const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

 // As explained in the Login page.
 const onFormInputChange = (event) => {
   const { name, value } = event.target;
   setForm({ ...form, [name]: value });
 };


 // As explained in the Login page.
 const redirectNow = () => {
   const redirectTo = location.search.replace("?redirectTo=", "");
   navigate(redirectTo ? redirectTo : "/");
 }

 // As explained in the Login page.
 const onSubmit = async (event) => {
    event.preventDefault();
    if (form.password !== form.confirmPassword) {
    alert("Passwords do not match.");
    return; // Prevent the form from submitting
    }

    if (!emailRegex.test(form.email)) {
      alert("Please enter a valid email address.");
      return; // Prevent the form from submitting
  }

   try {
    const user = await emailPasswordSignup(form.email, form.password, form.username);
     if (user) {
       redirectNow();
     }
   } catch (error) {
     alert(error);
   }
 };

 return <form style={{ display: "flex", flexDirection: "column", maxWidth: "300px", margin: "auto" }}>
   <h1>Signup</h1>
   <TextField
  label="Username"
  type="text"
  variant="outlined"
  name="username"
  value={form.username}
  onInput={onFormInputChange}
  style={{ marginBottom: "1rem" }}
/>

   <TextField
     label="Email"
     type="email"
     variant="outlined"
     name="email"
     value={form.email}
     onInput={onFormInputChange}
     style={{ marginBottom: "1rem" }}
   />
   <TextField
     label="Password"
     type="password"
     variant="outlined"
     name="password"
     value={form.password}
     onInput={onFormInputChange}
     style={{ marginBottom: "1rem" }}
   />
   <TextField // New Confirm Password TextField
      label="Confirm Password"
      type="password"
      variant="outlined"
      name="confirmPassword"
      value={form.confirmPassword}
      onInput={onFormInputChange}
      style={{ marginBottom: "1rem" }}
    />
   <Button variant="contained" color="primary" onClick={onSubmit}>
     Signup
   </Button>

 </form>
}

export default Signup;
