import { App, Credentials } from "realm-web";
import { APP_ID } from "../realm/constants";
import { createContext, useState, useContext } from "react";

const app = new App(APP_ID);


const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const emailPasswordLogin = async (email, password) => {
   const credentials = Credentials.emailPassword(email, password);
   const authenticatedUser = await app.logIn(credentials);
   setUser(authenticatedUser);
   return authenticatedUser;
  };

  const emailPasswordSignup = async (email, password, username) => { // Add username parameter
    try {
      const response = await fetch('http://localhost:3001/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password, username }),
        credentials: 'include' // Include credentials for cross-origin requests
      });

      if (!response.ok) {
        const errorDetails = await response.json(); // Assuming the server sends a JSON response with error details
        console.error("Signup failed:", errorDetails);
        throw new Error(`Signup failed: ${errorDetails.error || "Unknown error"}`);
      }


      const userData = await response.json(); // Assuming your endpoint responds with user data
      setUser(userData); // Update user state
    } catch (error) {
      console.error("Error during signup:", error);
      throw error;
    }
  };

  const fetchUser = async () => {
    if (!app.currentUser) return false;
    try {
      await app.currentUser.refreshCustomData();
      // Now, if we have a user, we are setting it to our user context
      // so that we can use it in our app across different components.
      setUser(app.currentUser);
      return app.currentUser;
    } catch (error) {
      throw error;
    }
  }

  // Function to logout user from our App Services app
  const logOutUser = async () => {
    if (!app.currentUser) return false;
    try {
      await app.currentUser.logOut();
      // Setting the user to null once loggedOut.
      setUser(null);
      return true;
    } catch (error) {
      throw error
    }
  }

  return (
    <UserContext.Provider value={{ user, setUser, emailPasswordLogin, emailPasswordSignup, fetchUser, logOutUser }}>
      {children}
    </UserContext.Provider>
  );
};

// Custom hook to use the UserContext
export const useUser = () => useContext(UserContext);
