import '../static/Home.Page.css'; // Import CSS file
import React, { useState, useEffect } from 'react';
import { useUser } from "../contexts/user.context";
import { Button, Card, CardContent, Typography, CardActions, Collapse } from '@mui/material';

export default function Home() {
  const { logOutUser } = useUser();

  const logOut = async () => {
    try {
      const loggedOut = await logOutUser();
      if (loggedOut) {
        window.location.reload(true);
      }
    } catch (error) {
      alert(error);
    }
  };

  const UserList = () => {
    const [users, setUsers] = useState([]);
    const [expandedUserId, setExpandedUserId] = useState(null); // New state to track expanded user card

    useEffect(() => {
      const fetchUsers = async () => {
        try {
          const response = await fetch('/api/users');
          if (!response.ok) throw new Error('Failed to fetch');
          const data = await response.json();
          setUsers(data);
        } catch (error) {
          console.error("Error fetching users:", error);
        }
      };

      fetchUsers();
    }, []);

    // Function to handle expanding/collapsing user details
    const handleExpandClick = (userId) => {
      setExpandedUserId(expandedUserId === userId ? null : userId);
    };

    return (
      <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
        {users.map(user => (
          <Card key={user._id} style={{ minWidth: '300px' }}>
            <CardContent onClick={() => handleExpandClick(user._id)} style={{ cursor: 'pointer' }}>
              <Typography variant="h5" component="div">
                {user.username}
              </Typography>
              <Typography variant="body2">
                Email: {user.email}
              </Typography>
            </CardContent>
            <Collapse in={expandedUserId === user._id} timeout="auto" unmountOnExit>
              <CardContent>
                <Typography paragraph>Account Creation Date: {new Date(user.accountCreationDate).toLocaleDateString()}</Typography>
                <Typography paragraph>Sessions:</Typography>
                {user.sessions && user.sessions.map((session, index) => (
                  <Typography key={index}>
                    Session ID: {session.sessionId}, Login Time: {new Date(session.loginTime).toLocaleString()}
                  </Typography>
                ))}
              </CardContent>
            </Collapse>
          </Card>
        ))}
      </div>
    );
  };

  const Navbar = () => {
    return (
      <nav className="navbar">
        <div className="navbar-left">
          <a href="#">Main Project</a>
        </div>
        <div className="navbar-right">
          <Button variant="contained" color="primary" onClick={logOut}>Logout</Button>
          <a className="Signup" href="/Signup" style={{ marginLeft: '10px' }}>Signup</a>
        </div>
      </nav>
    );
  };

  return (
    <>
      <div className="main-page">
        <Navbar />
        <UserList />
      </div>
    </>
  );
}
